import React from 'react';
import Chart from '../Chart/Chart';

const ExpensesChart=props=>{
    const ChartDatePoints=[
        {label:'Jan' , value:0},
        {label:'Feb' , value:0},
        {label:'Mar' , value:0},
        {label:'Apr' , value:0},
        {label:'May' , value:0},
        {label:'Jun' , value:0},
        {label:'Jul' , value:0},
        {label:'Aug' , value:0},
        {label:'Sep' , value:0},
        {label:'Oct' , value:0},
        {label:'NOv' , value:0},
        {label:'DEc' , value:0},
    ]

    for(const expense of props.expense){
        const expenseMonth=expense.date.getMonth();
        ChartDatePoints[expenseMonth].value+=expense.amount;
    }
    return <Chart dataPoints={ChartDatePoints}/>

}
export default ExpensesChart;
